mapper.select:
SELECT rno, bno, writer, content, regDate
FROM reply
WHERE bno = #{bno};

mapper.update:
UPDATE reply
SET writer = #{writer}, content = #{content} 
WHERE rno = #{rno} AND bno = #{bno};

mapper.select:
SELECT rno, bno, writer, content, regDate
FROM reply
WHERE bno=#{bno} AND rno=#{rno};

mapper.delete:
DELETE
FROM reply
WHERE rno = #{rno} AND bno = ${bno};
